import React, {useState} from "react";

function CreateArea(props){
  const [titleq, setTitle] = useState("");
  const [textq, setText] = useState("");

  function handleTitleChange(e){
    const titleInput = e.target.value;
    setTitle(titleInput);
  }
  function handleTextChange(e){
    const textInput = e.target.value;
    setText(textInput);
  }

  return (
    <form className="create-note">
      <input onChange={handleTitleChange} name="title" placeholder="Title" value={titleq} />
      <textarea onChange={handleTextChange} name="content" placeholder="Enter your notes" value={textq} />
      <button onClick={(evt)=>{
        props.addContent({titleq, textq});
        evt.preventDefault();
      }}>Add</button>
    </form>
  );
}

export default CreateArea;
