import React, { useState } from "react";

function CreateArea(props) {
  const [noteTitle, setNoteTitle] = useState("");
  const [textTitle, setTextTitle] = useState("");

  function handleNoteTitleChange(e) {
    const noteTitleValue = e.target.value;
    setNoteTitle(noteTitleValue);
  }

  function handleNoteTextChange(e) {
    const noteTextValue = e.target.value;
    setTextTitle(noteTextValue);
  }

  return (
    <div>
      <form>
        <input
          onChange={handleNoteTitleChange}
          name="title"
          placeholder="Title"
          value={noteTitle}
        />
        <textarea
          onChange={handleNoteTextChange}
          name="content"
          placeholder="Take a note..."
          rows="3"
          value={textTitle}
        />
        <button
          onClick={(evt) => {
            props.addContent({ noteTitle, textTitle });
            evt.preventDefault();
            setNoteTitle("");
            setTextTitle("");
          }}
        >
          Add
        </button>
      </form>
    </div>
  );
}

export default CreateArea;
