import React, {useState} from "react";

function CreateArea(props){
  const [title, setTitle] = useState("");
  const [text, setText] = useState("");

  function handleTitleChange(e){
    const titleInput = e.target.value;
    setTitle(titleInput);
  }
  function handleTextChange(e){
    const textInput = e.target.value;
    setText(textInput);
  }
  function handleClick(e){
    props.addContent({titleInput, textInput});
    e.preventDefault();
  }
  return (
    <form className="create-note">
      <input onChange={handleTitleChange} name="title" placeholder="Title" />
      <textarea onChange={handleTextChange} name="content" placeholder="Enter your notes" />
      <button onClick={handleClick}>Add</button>
    </form>
  );
}

export default CreateArea;
